import { React, useState } from 'react';
import { Form, Button } from 'react-bootstrap'
import DatePicker from "react-multi-date-picker";
import axios from 'axios'

const SignUpForm = () => {
  const [date, setDate] = useState(new Date())

  const handleSubmit = (e) => {
    //Consolidate form and only require required fields @ startup?
    e.preventDefault();
    const email = e.target.email.value;
    const full_name = e.target.full_name.value;
    const username = e.target.username.value;
    const password = e.target.password.value;
    const biography = e.target.biography.value;
    const roles = e.target.role.value;
    const availability = e.target.availability.value;

    // const user = {
    //   email,
    //   full_name,
    //   username,
    //   password,
    //   biography,
    //   role,
    //   availability
    // }

    const user = {
      email:email,
      full_name:full_name,
      username:username,
      password:password,
      biography:biography,
      roles:roles,
      availability: availability
    }

    config = {
      headers: {
        'Content-Type': 'application/json'
    }
    }
    axios.post('http://127.0.0.1:5000/user/sign_up', user, config)
    .then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
    });
  }
  return (
    <div>
      <h1>Sign Up</h1>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="email">
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" />
        </Form.Group>

        <Form.Group controlId="full_name">
          <Form.Label>Full Name</Form.Label>
          <Form.Control type="text" />
        </Form.Group>

        <Form.Group controlId="username">
          <Form.Label>Username</Form.Label>
          <Form.Control type="text" />
        </Form.Group>

        <Form.Group controlId="password">
          <Form.Label>Password</Form.Label>
          <Form.Control type="password" />
        </Form.Group>

        <Form.Group controlId="phone_number">
          <Form.Label>Phone Number</Form.Label>
          <Form.Control type="tel" pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}" required />
        </Form.Group>

        <Form.Group controlId="biography">
          <Form.Label>Tell us about yourself!</Form.Label>
          <Form.Control as="textarea" rows={3} />
        </Form.Group>

        <Form.Group controlId="role">
          <Form.Check
            inline
            value="tutor"
            name="role"
            label="Tutor"
            type="radio"
            id="tutor"
          />
          <Form.Check
            inline
            value="student"
            name="role"
            label="Student"
            type="radio"
            id="student"
          />
        </Form.Group>

        <Form.Group controlId="availability">
          <Form.Label>Availability</Form.Label>
          <DatePicker
            name="availability"
            multiple={true}
            value={date}
            onChange={setDate}
          />
        </Form.Group>

        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>
    </div>);
}

export default SignUpForm;
